mapper.select:
SELECT COUNT(*)
FROM chat_record
WHERE (sender_id = #{senderId} AND receiver_id = #{receiverId}
    OR sender_id = #{receiverId} AND receiver_id = #{senderId})
  AND send_time LIKE CONCAT(#{date}, '%')
  AND is_deleted = 0;

SELECT * FROM chat_record
WHERE  (sender_id = #{senderId} AND receiver_id = #{receiverId}
    OR sender_id = #{receiverId} AND receiver_id = #{senderId})
    AND send_time LIKE CONCAT(#{date}, '%')
    AND is_deleted = 0
LIMIT 2 OFFSET #{offset};

SELECT * FROM chat_record
WHERE (sender_id = #{senderId} AND receiver_id = #{receiverId}
    OR sender_id = #{receiverId} AND receiver_id = #{senderId})
    AND is_deleted = 0
ORDER BY send_time DESC
LIMIT 2 OFFSET #{offset};
