mapper.update:
UPDATE music_list
type = #{type}, user_id = #{user_id}, music_id = #{music_id}, create_date = #{create_date}, list_order = #{list_order}, description = #{description}, play_count = #{play_count}, is_deleted = #{is_deleted},
WHERE id = #{id}

mapper.select:
SELECT id, user_id, music_id, create_date, list_order, description, play_count, is_deleted
FROM music_list
WHERE id = #{id}
