mapper.update:
UPDATE music
title = #{title}, type = #{type}, writer = #{writer}, composer = #{composer}, producer = #{producer}, genre = #{genre}, artist = #{artist}, album = #{album}, release_date = #{release_date}, src = #{src}, duration = #{duration}, language = #{language}, likes = #{likes}, downloads = #{downloads}, lyrics = #{lyrics}, label = #{label}, play_count = #{play_count}, music_mood = #{music_mood},
WHERE id = #{id}

mapper.select:
SELECT id, title, type, writer, composer, producer, genre, artist, album, release_date, src, duration, language, likes, downloads, lyrics, label, play_count, music_mood
FROM music
WHERE id = #{id}
