mapper.update:
UPDATE music
title = #{title}, type = #{type}, writer = #{writer}, composer = #{composer}, producer = #{producer}, genre = #{genre}, artist = #{artist}, album = #{album}, release_date = #{releaseDate}, src = #{src}, duration = #{duration}, language = #{language}, likes = #{likes}, downloads = #{downloads}, lyrics = #{lyrics}, label = #{label}, play_count = #{playCount}, storage_time= #{storageTime},
WHERE id = #{id}

mapper.select:
SELECT id, title, type, writer, composer, producer, genre, artist, album, release_date, src, duration, language, likes, downloads, lyrics, label, play_count, music_mood, storage_time
FROM music
WHERE id = #{id}

SELECT *
FROM music
WHERE storage_time BETWEEN (NOW() - INTERVAL 24 HOUR) AND NOW();
