mapper.update:
UPDATE music_room_member
SET status = 'out'
WHERE user_id = #{userId} AND music_room_id = #{musicRoomId}

UPDATE music_room_member
SET status = 'in'
WHERE user_id = #{userId} AND music_room_id = #{musicRoomId}

UPDATE music_room_member
SET singable = 1
WHERE user_id = #{userId} AND music_room_id = #{musicRoomId}

UPDATE music_room_member
SET singable = 0
WHERE user_id = #{userId} AND music_room_id = #{musicRoomId}

UPDATE music_room_member
SET chatable = 1
WHERE user_id = #{userId} AND music_room_id = #{musicRoomId}

UPDATE music_room_member
SET chatable = 0
WHERE user_id = #{userId} AND music_room_id = #{musicRoomId}
